import{l as a}from"./chunks/log-created.75b11a39.js";import{_ as t,o as s,c as i,i as e,f as d}from"./app.b24a1b2d.js";const n={name:"yingxiangshijiedezhongguozhiwu",mixins:[a]},c={class:"container"},v=[e('<h1 class="title" data-v-18a9352a>影响世界的中国植物</h1><h5 class="author" data-v-18a9352a>《影响世界的中国植物》主创团队</h5><img src="/assets/t6_26914570.e2b076e7.jpg" alt="written" class="written" data-v-18a9352a><div class="wr_macOS wr_Desktop wr_page_reader wr_whiteTheme" data-v-18a9352a><div class="sectionListItem" data-v-18a9352a><div class="sectionListItem_title" data-v-18a9352a>宁可食无肉，不可居无竹</div><div class="sectionListItem_content noteItem_content clickable" data-v-18a9352a><span class="icon" data-v-18a9352a></span><div data-v-18a9352a><div class="text" data-v-18a9352a> 古今文人墨客，给竹子空心挺直、四季青翠、傲雪凌霜等特征赋予人格化的高洁、虚心、有节、刚直等精神意象，将其与梅、兰、菊并称为“四君子”，与梅、松并称为“岁寒三友”。正如英国学者李约瑟所说：东亚文明乃是“竹子文明”。 竹子终年常绿，象征清廉；竹杆通直富有韧性，象征守正不阿；空心，象征虚怀若谷；竹节，象征坚贞有节。 </div></div></div></div><div class="sectionListItem" data-v-18a9352a><div class="sectionListItem_title" data-v-18a9352a>蜀葵：以“医治”为名的丝路之花</div><div class="sectionListItem_content noteItem_content clickable" data-v-18a9352a><span class="icon" data-v-18a9352a></span><div data-v-18a9352a><div class="text" data-v-18a9352a> 蜀葵的传统寓意主要有两个：一个是与蜀葵较大的尺寸及盎然挺立的姿态相联系的高贵；另一个则是救赎。 </div></div></div></div></div>',4)];var o=t(n,[["render",function(a,t,e,d,n,o){return s(),i("div",c,v)}],["__scopeId","data-v-18a9352a"]]);const l='{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"read-notes/weread/yingxiangshijiedezhongguozhiwu.md"}',r={},_=Object.assign(r,{setup:a=>(a,t)=>(s(),i("div",null,[d(o)]))});export{l as __pageData,_ as default};
