import{l as d}from"./chunks/log-created.DGlCQ48K.js";import{_ as n,o as e,c as a,a2 as c,G as v}from"./chunks/framework.N5UAqUai.js";const o={name:"jishizhunquedechuandaxuqiu",mixins:[d]},l={class:"container"};function r(s,t,i,u,p,h){return e(),a("div",l,t[0]||(t[0]=[c('<h1 class="title" data-v-e1964693>及时且准确地传达需求</h1><div class="wr_macOS wr_Desktop wr_page_reader wr_whiteTheme" data-v-e1964693><div class="sectionListItem" data-v-e1964693><div class="sectionListItem_title" data-v-e1964693></div><div class="sectionListItem_content noteItem_content clickable" data-v-e1964693><span class="icon" data-v-e1964693></span><div data-v-e1964693><div class="text" data-v-e1964693> 对于程序员来说，有些产品经理确实很有吐槽点「说不清楚需求、替程序员“评估”工作量、频繁修改需求...」❌ <br data-v-e1964693> 产品经理日常工作都是围绕“需求”开始的，在和程序员对接上如果需求传达得不清不楚，导致技术各种返工，这其实是产品经理能力不专业的表现。 <br data-v-e1964693> 在与技术部门对接时，产品经理一定要“及时且准确地传达需求”，核心就是以下3⃣点： <br data-v-e1964693></div></div></div></div><div class="sectionListItem" data-v-e1964693><div class="sectionListItem_title" data-v-e1964693>一、不要“教人家做事”</div><div class="sectionListItem_content noteItem_content clickable" data-v-e1964693><span class="icon" data-v-e1964693></span><div data-v-e1964693><div class="text" data-v-e1964693> 注意：传达“需求”而非“技术实现方案”。产品经理当然需要与技术团队协商讨论具体的技术实现方案，但大前提是，明确将需求传达清楚。 </div></div></div></div><div class="sectionListItem" data-v-e1964693><div class="sectionListItem_title" data-v-e1964693>二、真的说清楚</div><div class="sectionListItem_content noteItem_content clickable" data-v-e1964693><span class="icon" data-v-e1964693></span><div data-v-e1964693><div class="text" data-v-e1964693> 注意：不要拐弯抹角，直接说清楚，并且有条理表述出来！<br data-v-e1964693> 顾左右而言他，不要下定义“这就是个小需求，很简单”反而会让程序员感到混乱，不清楚你的需求。 </div></div></div></div><div class="sectionListItem" data-v-e1964693><div class="sectionListItem_title" data-v-e1964693>三、传达“及时”</div><div class="sectionListItem_content noteItem_content clickable" data-v-e1964693><span class="icon" data-v-e1964693></span><div data-v-e1964693><div class="text" data-v-e1964693> 产品经理需要熟悉自己团队的协作流程，及时将需求传达给所有需要了解的人。如果需求传达不及时，导致开发进度出现问题，开发内容有误，就非常被动了。 </div></div></div></div></div>',2)]))}const _=n(o,[["render",r],["__scopeId","data-v-e1964693"]]),L=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"read-notes/zhiqun/jishizhunquedechuandaxuqiu.md","filePath":"read-notes/zhiqun/jishizhunquedechuandaxuqiu.md"}'),m={name:"read-notes/zhiqun/jishizhunquedechuandaxuqiu.md"},f=Object.assign(m,{setup(s){return(t,i)=>(e(),a("div",null,[v(_)]))}});export{L as __pageData,f as default};
